import { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setAmount] = useState("");
  const [enteredDate, setDate] = useState("");

  // const [userInput, setUserInput] = useState({
  //   enteredTitle: "",
  //   enteredAmount: "",
  //   enteredDate: "",
  // });

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
    //   setUserInput({
    //     ...userInput,
    //     eneteredTitle: event.target.value,
    //   });
  };

  const amountChangeHandler = (event) => {
    setAmount(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   eneteredAmount: event.target.value,
    // });

    //   setUserInput((prevState) => {
    //     return {...prevState, enteredTitle: event.target.value};
    //   });
  };

  const dateChangeHandler = (event) => {
    setDate(event.target.value);
    //   setUserInput({
    //     ...userInput,
    //     eneteredDate: event.target.value,
    //   });
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };

    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setAmount("");
    setDate("");
  };

  const formHandler = (state) => {
    showForm(state);
  };
  const addButton = (<button onClick={() => formHandler(formBox)}>Add New Expense</button>);

  const [form, showForm] = useState(addButton);
  const formBox = (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            value={enteredTitle}
            onChange={titleChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            value={enteredAmount}
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2021-01-01"
            max="2024-12-31"
            value={enteredDate}
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button onClick={() => formHandler(addButton)}>Cancel</button>
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );

  return (
    <div>
      {form}
    </div>
  );
};

export default ExpenseForm;
